mapper.select:
SELECT 

FROM weixin_menu a

WHERE a.id = #{id}

SELECT 

FROM weixin_menu a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.eventtype = #{eventtype}


		AND a.sort = #{sort}


		AND a.parent_id = #{parent.id}


		AND a.parent_ids = #{parentIds}


		AND a.group_id = #{groupId}


ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM weixin_menu a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE weixin_menu SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE weixin_menu SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
