mapper.select:
SELECT

FROM weixin_msg_news a

WHERE  b.inputcode = #{inputcode} AND b.account_id = #{accountid}

SELECT 

FROM weixin_msg_news a

WHERE a.id = #{id}

SELECT 

FROM weixin_msg_news a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.author = #{author}


		AND a.brief = #{brief}


		AND a.base_id = #{baseId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM weixin_msg_news a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
